import React, { Component } from 'react';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
} from 'chart.js'

ChartJS.register(
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
)



const options = {};

/*
data: {
    labels: [1, 2, 3],
    datasets: [{
        data: [4, 5, 7],
        backgroundColor: 'transparent',
        borderColor: '#f26c6d'
    }]
}
*/


export default class Graph extends Component {






	render() {
		return (
			<div>
				<br />

				<div className="title">Graph</div>
                <div style={{width: '500px', height: '500px', marginLeft: '20px'}}>
                    <Line data={this.props.graph.data} options={options}></Line>
                </div>

                <div>
                    {this.props.graph.data.labels}
                </div>

                {this.props.state.display.graph.column_names.map((data, i) => (
                    <div key={i} style={{width: '500px', height: '500px', marginLeft: '20px'}}>
                        <div className='title'>{data}</div>
                        <Line data={{
                            labels: this.props.state.display.graph.last_few_rows[0],
                            datasets: [{
                                data: this.props.state.display.graph.last_few_rows[i],
                                backgroundColor: 'transparent',
                                borderColor: '#f26c6d'
                            }]
                        }} optione={options}></Line>
                    </div>
                ))}
			</div>
		);
	}
}
