import React, { Component, useImperativeHandle } from 'react';
import Navigator from './misc/Navigator'

const default_state = {
	component: {
		navigator: 'hide'
	},
	button: {
		go_back: 'hide',
		navigator: 'hide'
	}
}

export default class Tracker extends Component {
	ws = new WebSocket('ws://localhost:8000/ws/websocket/' + 'tracker' + '/');

	componentDidMount() {
		this.ws.onopen = () => {
			console.log('connected');
		}
		this.ws.onmessage = (e) => {
			let response = JSON.parse(e.data).response;
			console.log(response);
			console.log(e);
		}
	}

	constructor(props) {
		super(props);
		this.state = default_state;
	}

	showNavigator = () => {
		this.setState((prevState) => {
			let button = Object.assign({}, prevState.button);
			let component = Object.assign({}, prevState.button);
			button.go_back = 'show';
			button.navigator = 'hide';
			component.navigator = 'show';
		})
	}

	render() {
		return (
			<div>
				<br />
				<div className="title">Tracker</div>

				<button
					className={this.state.button.navigator}
					onClick={this.showNavigator}
				>
					Launch Navigator
				</button>
				<div className={this.state.component.navigator}>
					<Navigator
						ws={this.ws}
					/>
				</div>


				<Navigator/>



			</div>
		);
	}
}
