import Graph from './display/Graph'
import Current from './display/Current'
import Info from './display/Info'


import React, { Component } from 'react';

class Display extends Component {
/*
    listDir = () => {
        console.log('Navigator.listDir(): STARTED');
        this.props.ws.send(
            JSON.stringify({
                request: 'listdir',
                data: {
                    location: ['navigator'],
                    path: this.props.navigator.path
                }
            })
        );
        console.log('Navigator.listDir(): FINISHED');
    };
*/

    connect = () => {
        console.log('Display.connect(): STARTED')


        let request = {
            location: ['Tracker', 'Display'],
            command: 'connect',
            data: ''
        }
		this.props.request(request);

        if (this.props.state.display.filename === 'none'){
            console.log('filename === none')

        } else {

            this.props.ws.send(
                JSON.stringify({
                    request: 'connect',
                    data: {
                        location: ['display'],
                        filename: this.props.display.filename,
                        path: this.props.display.path
                    }
                })
            );

        }


        console.log('Display.connect(): FINISHED')
    }

    render() {
        return (
            <div>
                <br/><br/>
                <div className='title'>Display</div>
                <br/><br/>

                <div>
                    <Info state={this.props.state}/>
                </div>

                <br/><br/><br/>
                <button onClick={this.connect}>Connect</button>
                <br/><br/><br/>

                <div>
                    <Current state={this.props.state}/>
                </div>
				<div>
					<Graph
						graph={this.props.state.graph}
					/>
				</div>

            </div>
        );
    }
}

export default Display;
